import React from "react";
import {Button, Image, StyleSheet, TextInput, View} from 'react-native';
import {globalStyle} from "../styles/style";
import {Formik} from "formik";
import { AntDesign } from '@expo/vector-icons';

export default function Login({navigation, addArticle}) {
    return (
        <View>
            <Formik initialValues={{login: '', password: ''}} onSubmit={(values, action) => {
                //addArticle(values);
                action.resetForm();
            }}>
                {(props) => (
                    <View>
                        <TextInput
                            style={styles.input}
                            value={props.values.login}
                            placeholder={'Введіть логін'}
                            placeholderTextColor={'silver'}
                            onChangeText={props.handleChange('login')}/>
                        <TextInput
                            style={styles.input}
                            value={props.values.password}
                            placeholder={'Введіть пароль'}
                            placeholderTextColor={'silver'}
                            onChangeText={props.handleChange('password')}
                            secureTextEntry={true}/>

                        <AntDesign name="login" style={{alignSelf:'center', margin:10}} size={30} color="#1847c7" onPress={()=> navigation.navigate('Main')}/>
                    </View>
                )}
            </Formik>
        </View>
    );
}

const styles = StyleSheet.create({
    input:
        {
            borderWidth: 2,
            marginTop: 15,
            padding: 10,
            borderColor: 'silver',
            borderRadius: 5,
            marginLeft: 5,
            marginRight: 5,
        }
});
